import React, { useReducer, useEffect, useCallback, useMemo } from 'react';

import IngredientForm from './IngredientForm';
import IngredientList from './IngredientList'
import Search from './Search';
import ErrorModal from '../UI/ErrorModal';

import useHttp from '../../hooks/http';

const ingredientReducer = (currentState, action) => {
  switch (action.type) {
    case 'SET':
      return action.ingredients;
    case 'ADD':
      return [...currentState, action.ingredient];
    case 'DELETE':
      return currentState.filter(ing => ing.id !== action.id);
    default:
      throw new Error('Something was wrong!');
  }
};

const Ingredients = () => {
  const [userIngredients, dispatch] = useReducer(ingredientReducer, []);
  const { isLoading, error, data, sendRequest } = useHttp();
  //const [userIngredients, setUserIngredients] = useState([]);
  // const [isLoading, setIsLoading] = useState(false);
  // const [error, setError] = useState(null);

  useEffect(() => {
    console.log('RENDER INGREDIEANTS', userIngredients);
  }, [userIngredients]);


  const addIngredientHandler = useCallback(ingredient => {
    // setIsLoading(true);
    dispatchError({ type: 'SEND' })
    fetch('https://gcamou-green-market.firebaseio.com/ingredients.json', {
      method: 'POST',
      body: JSON.stringify(ingredient),
      headers: { 'Content-Type': 'application/json' }
    }).then(response => {
      // setIsLoading(false);
      dispatchError({ type: 'RESPONSE' });
      return response.json();
    }).then(responseData => {
      dispatch(
        {
          type: 'ADD',
          ingredient: {
            id: responseData.name,
            ...ingredient
          }
        });
      //  setUserIngredients(prevIngredients => [
      //    ...prevIngredients,
      //    { id: responseData.name, ...ingredient }])
    }).catch(err => {
      console.log(err);
      // setError('Something was wrong!');
      // setIsLoading(false);
      dispatchError({ type: 'ERROR', error: 'Something was wrong!' });
    });
  }, []);

  const removeIngredientHandler = useCallback(id => {
    //setIsLoading(true);
    dispatchError({ type: 'SEND' });
    //            `https://gcamou-green-market.firebaseio.com/ingredients/${id}.json`, {

  }, []);

  const loadIngrediantsHandler = useCallback(ingredients => {
    dispatch({ type: 'SET', ingredients: ingredients });
    // setUserIngredients(ingredients);
  }, []);

  const closeModalHandler = useCallback(() => {
    // setError(null);
    dispatchError({ type: 'CLEAR' })
  }, []);

  const ingredientList = useMemo(() => {
    return (<IngredientList ingredients={userIngredients} onRemoveItem={removeIngredientHandler} />);
  }, [userIngredients, removeIngredientHandler]);

  return (
    <div className="App">
      {userHttpState.error && <ErrorModal onClose={closeModalHandler}>{userHttpState.error}</ErrorModal>}
      <IngredientForm onAddIngrediant={addIngredientHandler} isLoading={userHttpState.isLoading} />

      <section>
        <Search onLoadIngredients={loadIngrediantsHandler} />
        {ingredientList}
      </section>
    </div>
  );
}

export default Ingredients;
