import React, { useState } from 'react';

import IngredientForm from './IngredientForm';
import IngredientList from './IngredientList'
import Search from './Search';

const Ingredients = () => {
  const [userIngredients, setUserIngredients] = useState([])

  const addIngredientHandler = ingredient => {
    setUserIngredients(prevIngredients => [
      ...prevIngredients,
      { id: Math.random().toString(), ...ingredient }])
  }

  const removeItemHandler = id => {
    const newIngredients = { ...userIngredients }
    const updateIngrediente = newIngredients.filter(ig => ig.id !== id)
    setUserIngredients(updateIngrediente)
  }

  return (
    <div className="App">
      <IngredientForm onAddIngrediant={addIngredientHandler} />

      <section>
        <Search />
        <IngredientList ingredients={userIngredients} onRemoveItem={removeItemHandler} />
      </section>
    </div>
  );
}

export default Ingredients;
