import React, { useState, useEffect } from 'react';

import Card from '../UI/Card';
import './Search.css';

const Search = React.memo(props => {
  const { onLoadIngrediants } = props;
  const [enteredFilter, setEnteredFilter] = useState('');

  useEffect(() => {
    const query = enteredFilter.length <= 0
      ? ''
      : `?orderBy="title"&equalTo="${enteredFilter}"`;
    fetch('https://gcamou-green-market.firebaseio.com/ingredients.json' + query)
      .then(response => response.json())
      .then(responseData => {
        let loadIngrediants = [];
        for (let key in responseData) {
          loadIngrediants.push({
            id: key,
            title: responseData[key].title,
            amount: responseData[key].amount
          });
        }
        onLoadIngrediants(loadIngrediants);
      });
  }, [enteredFilter, onLoadIngrediants]);

  return (
    <section className="search">
      <Card>
        <div className="search-input">
          <label>Filter by Title</label>
          <input type="text" value={enteredFilter} onChange={event => { setEnteredFilter(event.target.value) }} />
        </div>
      </Card>
    </section>
  );
});

export default Search;
