import React, { useState, useEffect, useCallback } from 'react';

import IngredientForm from './IngredientForm';
import IngredientList from './IngredientList'
import Search from './Search';

const Ingredients = () => {
  const [userIngredients, setUserIngredients] = useState([])

  useEffect(() => {
    console.log('RENDER INGREDIEANTS', userIngredients);
  }, [userIngredients]);


  const addIngredientHandler = ingredient => {
    fetch('https://gcamou-green-market.firebaseio.com/ingredients.json', {
      method: 'POST',
      body: JSON.stringify(ingredient),
      headers: { 'Content-Type': 'application/json' }
    }).then(response => {
      return response.json();
    }).then(responseData => {
      setUserIngredients(prevIngredients => [
        ...prevIngredients,
        { id: responseData.name, ...ingredient }])
    });
  }

  const removeIngredientHandler = id => {
    const newIngredients = [...userIngredients]
    const updateIngrediente = newIngredients.filter(ig => ig.id !== id)
    setUserIngredients(updateIngrediente)
  }

  const loadIngrediantsHandler = useCallback(ingredients => {
    setUserIngredients(ingredients);
  }, [])

  return (
    <div className="App">
      <IngredientForm onAddIngrediant={addIngredientHandler} />

      <section>
        <Search onLoadIngredients={loadIngrediantsHandler} />
        <IngredientList ingredients={userIngredients} onRemoveItem={removeIngredientHandler} />
      </section>
    </div>
  );
}

export default Ingredients;
