import React, { useState, useEffect, useCallback } from 'react';

import IngredientForm from './IngredientForm';
import IngredientList from './IngredientList'
import Search from './Search';
import ErrorModal from '../UI/ErrorModal';

const Ingredients = () => {
  const [userIngredients, setUserIngredients] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('RENDER INGREDIEANTS', userIngredients);
  }, [userIngredients]);


  const addIngredientHandler = ingredient => {
    setIsLoading(true);
    fetch('https://gcamou-green-market.firebaseio.com/ingredients.', {
      method: 'POST',
      body: JSON.stringify(ingredient),
      headers: { 'Content-Type': 'application/json' }
    }).then(response => {
      setIsLoading(false);
      return response.json();
    }).then(responseData => {
      setUserIngredients(prevIngredients => [
        ...prevIngredients,
        { id: responseData.name, ...ingredient }])
    }).catch(err => {
      setError('Something was wrong!');   
       setIsLoading(false);
    });
  }

  const removeIngredientHandler = id => {
    setIsLoading(true);
    fetch(`https://gcamou-green-market.firebaseio.com/ingredients/${id}.json`, {
      method: 'DELETE'
    }).then(() => {
      setIsLoading(false);
      setUserIngredients(prevIngredients => prevIngredients.filter(ig => ig.id !== id))
    }).catch(err => {
      setError('Something was wrong!');   
       setIsLoading(false);
    });
  }

  const loadIngrediantsHandler = useCallback(ingredients => {
    setUserIngredients(ingredients);
  }, []);

  const closeModalHandler = () => {
    setError(null);
  };

  return (
    <div className="App">
      {error && <ErrorModal onClose={closeModalHandler}>{error}</ErrorModal>}
      <IngredientForm onAddIngrediant={addIngredientHandler} isLoading={isLoading} />

      <section>
        <Search onLoadIngredients={loadIngrediantsHandler} />
        <IngredientList ingredients={userIngredients} onRemoveItem={removeIngredientHandler} />
      </section>
    </div>
  );
}

export default Ingredients;
